class LeafNode extends TreeNode {

	charInfo = {
			type: ["Mage", "Rogue", "Tank", "Archer"],
			abilities: [[0,1], [0,1,2], [0,1], [0,1,2]],
			// teamId
			abilityTargets: [[1,0], [1,1,-1], [1,-1], [1,1,1]]
	}

	public override async execute(rg): Promise<NodeStatus>{		

		// The character type we request may not be the one we actually get
		const characterType = rg.characterConfig.characterType;
		if (characterType) {
			const newCharType = this.charInfo.type.indexOf(characterType);
			this.setData("charType", newCharType)
			return NodeStatus.SUCCESS;
  	}

		console.error("NO CHARACTER TYPE");
		return NodeStatus.FAILURE;
	}
}

class LeafNode extends TreeNode {
	public override async execute(rg): Promise<NodeStatus>{		
		// if the bot is standing on a switch, then do nothing
  	const floorSwitch = await rg.findEntity("FloorSwitch");
		if(floorSwitch) {
			this.setData("floorSwitch", floorSwitch);
			return NodeStatus.SUCCESS;
		}

		return NodeStatus.FAILURE;
	}
}

class LeafNode extends TreeNode {
	public override async execute(rg): Promise<NodeStatus>{		
		const floorSwitch = this.getData<any>("floorSwitch");

		// already standing on it
		if(floorSwitch && await rg.entityHasAttribute(floorSwitch, "isOn", true)) {
			return NodeStatus.SUCCESS;
		}

		// otherwise go to it
		if(floorSwitch && rg.MathFunctions.distanceSq(rg.getBot().position, floorSwitch.position) < 30) {
			rg.performAction("FollowObject", {
				targetId: floorSwitch.id,
				range: 0.1
			});
			return NodeStatus.RUNNING;
  	}

		return NodeStatus.FAILURE
	}
}

