	}
}

protected override NodeStatus Execute(RG rgObject)
{
	var floorSwitch = rgObject.FindNearestEntity("FloorSwitch");
	if (floorSwitch != null && !(bool)floorSwitch["isOn"])
	{
		SetData("FloorSwitch", floorSwitch);
		return NodeStatus.Success;
	}
	return NodeStatus.Failure;
}

protected override NodeStatus Execute(RG rgObject)
{
	var floorSwitch = GetData<RGStateEntity>("FloorSwitch");
	var myPlayer = rgObject.GetMyPlayer();

	if (RG.MathFunctions.DistanceSq(myPlayer.position, floorSwitch.position) < 0.5)
		return NodeStatus.Running;

	var followAction = new RGActionRequest("FollowObject", new Dictionary<string, object>
	{
		{ "targetId", floorSwitch.id },
		{ "range", 0.25f }
	});

	rgObject.PerformAction(followAction);

	return NodeStatus.Success;
}

protected override NodeStatus Execute(RG rgObject)
{
	var human = rgObject.FindNearestEntity("HumanPlayer");
	if(human == null) return NodeStatus.Failure;
	if(RG.MathFunctions.DistanceSq(rgObject.GetMyPlayer().position, human.position) > 50)
	{
		SetData("closestHuman", human);
		return NodeStatus.Success;
	}
	return NodeStatus.Failure;
}

protected override NodeStatus Execute(RG rgObject)
{
	var closestHuman = rgObject.FindNearestEntity("HumanPlayer");
	if (closestHuman == null)
		return NodeStatus.Failure;

	rgObject.PerformAction(new RGActionRequest("FollowObject", new Dictionary<string, object> { { "targetId", closestHuman.id }, { "range", 2 } }));
	return NodeStatus.Success;
}

protected override NodeStatus Execute(RG rgObject)
{
	var entity = rgObject.FindNearestEntity(filterFunction: e => e.ContainsKey("team") && (int)e["team"] == 1 && (!e.ContainsKey("broken") || !(bool)e["broken"]));
	if (entity != null)
	{
		SetData("entity", entity);
		return NodeStatus.Success;
	}
	else
	{
		return NodeStatus.Failure;
	}
}

protected override NodeStatus Execute(RG rgObject)
{
	var nearestEntity = GetData<RGStateEntity>("nearestEntity");
	if(nearestEntity == null)
		return NodeStatus.Failure;

	var actionParams = new Dictionary<string, object>
	{
		{"targetId", nearestEntity.id},
		{"xPosition", nearestEntity.position.x},
		{"yPosition", nearestEntity.position.y},
		{"zPosition", nearestEntity.position.z}
	};

	rgObject.PerformAction(new RGActionRequest("AttackObject", actionParams));
	return NodeStatus.Success;
}

