protected override NodeStatus Execute(RG rgObject)
{
	var myPlayer = rgObject.GetMyPlayer();
	if(myPlayer.ContainsKey("characterType"))
	{
		SetData("characterType", myPlayer["characterType"]);
		return NodeStatus.Success;
	}
	else
	{
		return NodeStatus.Failure;
	}
}

protected override NodeStatus Execute(RG rgObject)
{
	var floorSwitch = rgObject.FindNearestEntity("FloorSwitch", rgObject.GetMyPlayer().position, e => !(bool)e.GetValueOrDefault("isOn", false));
	if(floorSwitch == null) return NodeStatus.Failure;

	if(RG.MathFunctions.DistanceSq(rgObject.GetMyPlayer().position, floorSwitch.position) <= 30)
	{
		SetData("floorSwitch", floorSwitch);
		return NodeStatus.Success;
	}
	return NodeStatus.Failure;
}

protected override NodeStatus Execute(RG rgObject)
{
	var floorSwitch = GetData<RGStateEntity>("floorSwitch");
	rgObject.PerformAction(new RGActionRequest("FollowObject", new Dictionary<string, object> { { "targetId", floorSwitch.id } }));
	return NodeStatus.Success;
}

protected override NodeStatus Execute(RG rgObject)
{
	var human = rgObject.FindNearestEntity("HumanPlayer");
	if(human == null) return NodeStatus.Failure;

	if(RG.MathFunctions.DistanceSq(rgObject.GetMyPlayer().position, human.position) > 30)
	{
		SetData("closestHuman", human);
		return NodeStatus.Success;
	}
	return NodeStatus.Failure;
}

protected override NodeStatus Execute(RG rgObject)
{
	var entity = rgObject.FindNearestEntity(filterFunction: e => 
		(int)e.GetValueOrDefault("team", 0) == 1 && 
		!(bool)e.GetValueOrDefault("broken", false) && 
		(int)e.GetValueOrDefault("health", 0) > 0);
	if (entity != null)
	{
		SetData("nearestEntity", entity);
		return NodeStatus.Success;
	}
	else
	{
		return NodeStatus.Failure;
	}
}

protected override NodeStatus Execute(RG rgObject)
{
	var nearestEntity = GetData<RGStateEntity>("nearestEntity");
	if (nearestEntity == null)
	{
		return NodeStatus.Failure;
	}

	var actionParams = new Dictionary<string, object>
	{
		{"skillId", 0},
		{"targetId", nearestEntity.id}
	};

	rgObject.PerformAction(new RGActionRequest("PerformSkill", actionParams));

	return NodeStatus.Success;
}

protected override NodeStatus Execute(RG rgObject)
{
	if (rgObject.SceneName != "BossRoom" || rgObject.GetMyPlayer() == null)
		return NodeStatus.Running;
	else
		return NodeStatus.Success;
}

protected override NodeStatus Execute(RG rgObject)
{
	var myPlayer = rgObject.GetMyPlayer();
	if ((bool)myPlayer["hasQueuedActions"])
		return NodeStatus.Running;
	else
		return NodeStatus.Success;
}

protected override NodeStatus Execute(RG rgObject)
{
	var myPlayer = rgObject.GetMyPlayer();
	var floorSwitch = rgObject.FindNearestEntity("FloorSwitch", myPlayer.position, e => (bool)e.GetValueOrDefault("isOn", false));
	if (floorSwitch != null && RG.MathFunctions.DistanceSq(myPlayer.position, floorSwitch.position) <= 0.1)
		return NodeStatus.Running;
	else
		return NodeStatus.Failure;
}
